import NewTask from './components/NewTask/NewTask';

function App() {

  const requestConfig = {
    url: 'https://react-custom-hooks-9591b-default-rtdb.asia-southeast1.firebasedatabase.app/tasks.json',
    method: 'GET',
    body: null
  }

  const dataProcessor = () => {
    const loadedTasks = [];

