import React, { useState } from 'react';
import Button from "./components/UI/Button/Button";

import './App.css';

function App() {

  const [paraIsShown, setParaIsShown] = useState(true);

  const toggleButtonHandler = () => {
    setParaIsShown((prevBtnState) => !prevBtnState);
  }

  const hideButtonHandler = () => {
    setParaIsShown(false);
  }

  return (
    <div className="app">
      <h1>Hi there!</h1>
      {paraIsShown && <p>Hi, This is new text!</p>}
      <Button type="button" onClick={toggleButtonHandler} disabled={hideButtonHandler}>Click me</Button>
    </div>
  );
}

export default App;
